INSERT INTO `text` VALUES(152, 'There are two means of refuge from the miseries of life: music and cats.', '#1d3fd4', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(153, 'Iâ€™m not saying that everything is survivable. Just that everything except the last thing is.', '#e72b8a', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(154, 'The fear of death follows from the fear of life. A man who lives fully is prepared to die at any time.', '#9bfe3', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(155, 'Out of clutter, find simplicity, from discard, find harmony, in the middle of difficulty lies opportunity.', '#2ccd', '127.0.0.1', '', '', '', '', 2, '', '', '');
