INSERT INTO `text` VALUES(339, 'Iâ€™m not saying that everything is survivable. Just that everything except the last thing is.', '#8ac37d', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(340, 'The fear of death follows from the fear of life. A man who lives fully is prepared to die at any time.', '#66deb', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(341, 'Out of clutter, find simplicity, from discard, find harmony, in the middle of difficulty lies opportunity.', '#7140e7', '127.0.0.1', '', '', '', '', 2, '', '', '');
