INSERT INTO `text` VALUES(376, 'I wanted the whole world or nothing.', '#a9bcca', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(377, 'And if you feel that you canâ€™t go on, in the light you will find the road.', '#304215', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(378, 'Love is why I came here in the first place.', '#de9994', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(379, 'Twenty years from now you will be more disappointed by the things that you didnâ€™t do than by the ones you did do. So throw off the bowlines. Sail away from the safe harbor. Catch the trade winds in your sails. Explore. Dream. Discover.', '#9c896e', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(380, 'Things get bad for all of us, almost continually, and what we do under the constant stress reveals who/what we are.', '#d15b4b', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(381, 'There is no safe investment. To love at all is to be vulnerable.', '#65d754', '127.0.0.1', '', '', '', '', 2, '', '', '');
