INSERT INTO `text` VALUES(383, 'Those who can laugh without cause have either found the true meaning of happiness or have gone stark raving mad.', '#30ef52', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(384, 'More people write poetry than read it.', '#4cb789', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(385, 'Be swift to love, make haste to be kind', '#6495e7', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(386, 'I hadnâ€™t been paying much attention to things like the sunrise, but that old sun had been coming up anyway. It didnâ€™t really care how I felt, it was going to rise and set regardless of whether I noticed it, and if I was going to enjoy it, that was up to me.', '#201055', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(387, 'Youth cannot know how age thinks and feels. But old men are guilty if they forget what it was to be young.', '#a6313a', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(388, 'The moment you get tied up in thinking about how to manage a community, you can no longer lead from within that community. To really do this right, you simply must love the platform, love the community, and wish to inspire both to greatness.', '#a17f7e', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(389, 'Happiness is a mood, not a destination', '#3738b', '127.0.0.1', '', '', '', '', 2, '', '', '');
