INSERT INTO `text` VALUES(437, 'I hated every minute of training, but I said, â€˜Donâ€™t quit. Suffer now and live the rest of your life as a championâ€™.', '#23e80', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(438, 'Heavenâ€™s not a place that you go when you die, itâ€™s that moment in life when you actually feel alive, so live for the moment. Take this advice, live by every word, love is just a hoax so forget anything that you have heard and live for the moment now.', '#cb46c4', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(439, 'We are not mad. We are human.We want to love, and someone must forgive us for the paths we take to love, for the paths are many and dark, and we are ardent and cruel in our journey.', '#8e8a5e', '127.0.0.1', '', '', '', '', 2, '', '', '');
