INSERT INTO `text` VALUES(957, 'Worry is a misuse of imagination.', '#49f75e', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(958, 'This world is but a canvas to our imaginations.', '#b25677', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(959, 'Nothing thatâ€™s worth having ever comes easy.', '#31487e', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(960, 'Rather than love, than money, than fame, give me truth.', '#f25115', '127.0.0.1', '', '', '', '', 2, '', '', '');
INSERT INTO `text` VALUES(961, 'All growing up means is that you realize no one will come along to fix things. No one will come along to save you.', '#6919bf', '127.0.0.1', '', '', '', '', 2, '', '', '');
