INSERT INTO `text` VALUES(3559, 'I got a flyer in the mail that said â€œGet into a new car no questions asked!â€ Bullshitâ€¦ my neighbor still asked WTF I was doing in his BMW.', '#f3c4df', '83.212.104.94', '', '', '', 'Apple Gothic', 3, '', '', '');
