INSERT INTO `text` VALUES(3563, '"So I was on a spaceship, and you were there, but it was not you, you know? Anyways..." - MLK''s less important dream', '#53f920', '83.212.104.94', '', '', '', 'Oldtown', 5, '', '', '');
