INSERT INTO `text` VALUES(3565, 'The good news is, I found my phone charger in my bed while I was vacuuming my sheets. The bad news is, you read that 2nd part correctly.', '#9f8280', '83.212.104.94', '', '', '', 'Futura', 1, '', '', '');
