INSERT INTO `text` VALUES(3567, 'Just downloaded the MySpace app and my iPad transformed into a Commodore 64.', '#502545', '83.212.104.94', '', '', '', 'New Century Schoolbook', 3, '', '', '');
INSERT INTO `text` VALUES(3568, 'The number of times you''ve found a hair in your food is not nearly as scary as the number of times you haven''t.', '#b6231d', '83.212.104.94', '', '', '', 'Century Gothic', 5, '', '', '');
INSERT INTO `text` VALUES(3569, 'There''s literally no way to know how many chameleons are in your house', '#2691ab', '83.212.104.94', '', '', '', 'Fixed', 4, '', '', '');
