INSERT INTO `text` VALUES(4220, 'Just the good ol'' boys, never meanin'' no harm. Beats all you''ve ever saw, been in trouble with the law since the day they was born. ', '#aafa5', '37.32.254.225', '', '', '', 'Fixed', 4, '', '', '');
